import React from "react";
import SideBar from "../components/sidebar";
import axios from "axios";
import { useState , useEffect} from "react";

const ManageTrips = () =>{
    function getTripItem(e){
        let item = {[e.target.name]:e.target.value};
        e.preventDefault()
        axios.post("/api/get-trip-item", item).then(res =>{
            console.log(res.data)
        })
    }
    return(
        <>
        <div className="container-fluid">
        <div className="row flex-nowrap">
            <SideBar />
            <div className="col py-5 col-md-9 admin-content">
            <h3 className="text-center home-1st-text text-dark">
                ROUTES, TRIPS & VEHICLE MANAGEMENT <i className="bi bi-sliders"></i>
            </h3>
            <hr />
            <div className="col-md-3">
                <b>Select Item to Manage: </b>
                <select className="form-select" name="tripItem" required onChange={getTripItem}>
                    <option value=""></option>
                    <option value="routes_tbl">Manage Routes</option>
                    <option value="trip_tbl">Manage Trips</option>
                    <option value="vehicle_tbl">Manage Vehicles</option>
                </select>
            </div>

        </div>
        </div>
        </div> 
        </>
    )
}

export default ManageTrips;
