import React, { useState } from "react";
import { Col, Row} from "react-bootstrap";
import Header from '../components/header';
import Footer from '../components/footer';
import { Navigate, useLocation, useNavigate} from "react-router-dom";
import axios from "axios";
import { useEffect } from "react";

const BookingData = () =>{
    const location = useLocation();
    const history = useNavigate()

    if(location.state)
    {
        const data = location.state;
        const [searchTerm, setSearchTerm] = useState({
            seats: location.state.seats,
            total_fare: location.state.total_fare,
            trip_id: location.state.trip_id
        });
        const months = ["January", "February", "March", "April", "May", "June", "July", "August", "September", "October", "November", "December"];
        const d = new Date(data.trip_date);
        let month = months[d.getMonth()];
        const year = d.getFullYear();
        const day = d.getDate()

        return(
            <>
            <Header />
            <div className="content">
                    <Row>
                        <Col md={1}></Col>
                        <Col md={10} className="middle-content">
                            <div className="row alert alert-info trip-schedule2">
                                <div className="col">
                                    <h3>Boarding  Details</h3>
                                    <table className="table booking-data">
                                        <tr>
                                            <td>From</td>
                                            <td><span>{data.trip_from}</span></td>
                                        </tr>
                                        <tr>
                                            <td>To</td>
                                            <td><span>{data.trip_to}</span></td>
                                        </tr>
                                        <tr>
                                            <td>Date/Time</td>
                                            <td>
                                                <span>{month} {day}, {year}</span>  <i className="bi-arrow-right"> </i>  
                                                <span>{
                                                        new Date(data.trip_time).getHours() >11 
                                                        ? new Date(data.trip_time).getHours() +':'+ new Date(data.trip_time).getMinutes() + 'PM'
                                                        : new Date(data.trip_time).getHours() +':'+ new Date(data.trip_time).getMinutes() + 'AM'
                                                        }
                                                </span>
                                            </td>
                                        </tr>
                                        <tr>
                                            <td>Shuttle</td>
                                            <td><span>{data.vehicle_name}</span></td>
                                        </tr>
                                        <tr>
                                            <td>Seat No</td>
                                            <td><span>{data.seats.join(', ')}</span></td>
                                        </tr>

                                    </table>
                                </div>
                                <div className="col-md-5 booking-data">
                                    <h3>Boarding  Details</h3>
                                                        <table className="table">
                                                            <tr>
                                                                <td>Ticket Fare:</td>
                                                                <td className="aln-r">
                                                                    <del className="naira">N</del>
                                                                    {(data.trip_fare * selectedSeats.length).toLocaleString()}
                                                                </td>
                                                            </tr>
                                                            <tr>
                                                                <td>Grand Total:</td>
                                                                <td className="aln-r">
                                                                    <del className="naira">N</del>
                                                                    {(data.trip_fare * selectedSeats.length).toLocaleString()}
                                                                </td>
                                                            </tr>
                                                            <tr style={{'fontWeight':'bold'}}>
                                                                <td>Payable Amount:</td>
                                                                <td className="aln-r">
                                                                        <del className="naira">N</del>
                                                                    {(data.trip_fare * selectedSeats.length).toLocaleString()}
                                                                </td>
                                                            </tr>
                                                        </table>
                                                    </div>
                            </div>
                                {
                                    data.seats.map(seat =>
                                        <div className="row">
                                            <div className="seat_no">Seat No: {seat}</div>
                                            <form className="form-inline">
                                                <div className="mt-3 mb-3 col-6">
                                                    <label htmlFor="fname">Full Name</label>
                                                    <input className="form-control" type="text" name="firstName" id="fname"/>
                                                </div>
                                                <div className="mt-3 mb-3 col-6">
                                                    <label htmlFor="fname">Last Name</label>
                                                    <input className="form-control" type="text" name="firstName" id="fname"/>
                                                </div>
                                            </form>
                                        </div>
                                    )
                                }

                        </Col>
                        <Col md={1}></Col>
                    </Row>
            </div>
            <Footer />
            </>
        ) }
        else{ 
        return (<Navigate to="/booking" />) }

    }

export default BookingData;
