import React, { useEffect, useState } from "react";
import {Form, FormLabel, FormGroup, } from "react-bootstrap";
import SideBar from "../components/sidebar";
import  {StateLGA} from '../data/stateLGAs';
import axios from "axios";
//import Booking from "../components/bookingtab";
var todayDate = new Date().toISOString().slice(0, 10);

const AddStaff = () =>{
    const sortedStates = StateLGA.sort((a,b) => a.state > b.state ? 1:-1);
    const [stateLga, setStateLGA] = useState([])
    const [dept, setDept] = useState([])
    const [pos, setPos] = useState([])
    const [values, setValues] = useState({
        staffCode:'',
        firstName:'',
        middleName: '',
        lastName:'',
        dob: '',
        sex: '',
        lga: '',
        state: '',
        address: '',
        email: '',
        phone: '',
        dept: '',
        pos: '',
        jobTitle:'',
    })

    const findLGA = (e)=>{
        if(e.target.value !== "")
        {
            const query = sortedStates.find(state => state.state === e.target.value);
            const result = query.lgas.sort()
            setStateLGA(result);
        }
        else{
            setStateLGA([]);
        }
    }
useEffect(() =>{
    axios.get("/api/get-departments").then((res) =>{
            setDept(res.data.dept)
        });

        axios.post("/api/get-positions").then((res) =>{
            setPos(res.data.positions)
        })
}, []);


const setInfo = (e) =>{
    setValues({...values, [e.target.name]:e.target.value});
}
return(
    <>

    <div className="container-fluid">
        <div className="row flex-nowrap">
            <SideBar />
            <div className="col py-5 col-md-7 admin-content">
            <h3 className="text-center">ADD STAFF</h3>
            <Form method="POST" action="/add-staff">

                        <div className="form-item row">
                            <div className="col">
                                <FormGroup>
                                    <FormLabel>First Name: </FormLabel>
                                    <Form.Control type="text" name="firstName" required value={values.firstName} onChange={setInfo} />
                                </FormGroup>
                            </div>
                            <div className="col">
                                <FormGroup>
                                    <FormLabel>Last Name: </FormLabel>
                                    <Form.Control type="text" name="lastName" required value={values.lastName} onChange={setInfo} />
                                </FormGroup>
                            </div> 
                        </div>
                        <div className="form-item row">
                            <div className="col">
                                <FormGroup>
                                    <FormLabel>Date of Birth:</FormLabel>
                                    <Form.Control type="date" name="dob" required max={todayDate} value={values.dob} onChange={setInfo} />
                                </FormGroup>
                            </div>
                            <div className="col">
                                <FormLabel>Sex:</FormLabel>
                                <Form.Select aria-label="Default select example" name="sex" required value={values.sex} onChange={setInfo} >
                                    <option value=""></option>
                                    <option value="f">Male</option>
                                    <option value="m">Female</option>
                                </Form.Select>
                            </div> 
                        </div>

                        <div className="form-item row">
                            <div className="col">
                                <FormLabel>State of Origin:</FormLabel>
                                <Form.Select aria-label="Default select example" name="state" required onChange={e => {findLGA(e); setInfo(e)}}>
                                <option></option>
                                {
                                    sortedStates.map(stateItem =>

                                        <option value={stateItem.state}>{stateItem.state}</option>

                                    )
                                }
                                </Form.Select>
                            </div>
                            <div className="col">
                                <FormLabel>LGA:</FormLabel>
                                <Form.Select aria-label="Default select example" name="lga" required>
                                {
                                    stateLga.map(lga =>

                                        <option value={lga}>{lga}</option>

                                    )
                                }
                                </Form.Select>
                            </div> 

                        </div>

                        <div className="form-item row">
                            <div className="col">
                                <FormLabel>Email:</FormLabel>
                                <Form.Control type="email" name="email" required placeholder="Enter email">

                                </Form.Control>
                            </div>
                            <div className="col">
                            <FormLabel>Phone:</FormLabel>
                                <Form.Control type="tel" name="email" required placeholder="Enter Phone Number">

                                </Form.Control>
                            </div> 

                        </div>

                        <div className="form-item">
                        <FormLabel>Address: </FormLabel>
                        <Form.Control as="textarea" rows={4}  name="address" required className="w-60">

                        </Form.Control>
                        </div>

                        <div className="form-item row">
                            <div className="col">
                                <FormLabel>Unit / Department:</FormLabel>
                                <Form.Select aria-label="Default select example" name="dept" required onChange={setInfo}>
                                <option></option>
                                {
                                    dept.map(deptItem =>

                                        <option value={deptItem.dept_name}>{deptItem.dept_name}</option>

                                    )
                                }
                                </Form.Select>
                            </div>
                            <div className="col">
                                <FormLabel>Position:</FormLabel>
                                <Form.Select aria-label="Default select example" name="pos" required>
                                {
                                    pos.map(pos =>

                                        <option value={pos}>{pos.positions}</option>

                                    )
                                }
                                </Form.Select>
                            </div> 

                        </div>
                        <br />
                        <div className="form-item row">
                            <FormGroup>
                            <Form.Control type="submit"  name="addBooking" id="btn-book" value="Proceed" />
                            </FormGroup>
                        </div>
                    </Form>
        </div>
        </div>
        </div> 
    </>
)
}

export default AddStaff
