import React, { useState } from "react";
import { Col, Row} from "react-bootstrap";
import Header from '../components/header';
import Footer from '../components/footer';
import { Navigate, useLocation, useNavigate} from "react-router-dom";
import axios from "axios";
import { useEffect } from "react";

const BookingData = () =>{
    const location = useLocation();
    const history = useNavigate()

    if(location.state)
    {
        const data = location.state;

        const months = ["January", "February", "March", "April", "May", "June", "July", "August", "September", "October", "November", "December"];
        const d = new Date(data.trip_date);
        let month = months[d.getMonth()];
        const year = d.getFullYear();
        const day = d.getDate()
        let b_data_array = [];
        data.seats.map(seat =>{
            let dd = {
                fullname: '',
                age: '',
                sex: '',
                seat_no: ''
            }
            b_data_array.push(dd)
        })
        const [booking, setBooking] = useState(b_data_array);

        const handleInputChange = (index, event) =>{
            const values = [...booking];
            const updatedValue = event.target.name;
            values[index][updatedValue] = event.target.value;

            setBooking(values);
        };

        //console.log(booking)

        function submit(){
            console.log(booking);
        }

        return(
            <>
            <Header />
            <div className="content">
                    <Row>
                        <Col md={1}></Col>
                        <Col md={10} className="middle-content">
                            <div className="row alert alert-info trip-schedule2">
                                <div className="col booking-details">
                                    <h4>Booking  Details</h4>
                                    <table className="table">
                                        <tr>
                                            <td>From</td>
                                            <td><span>{data.trip_from}</span></td>
                                        </tr>
                                        <tr>
                                            <td>To</td>
                                            <td><span>{data.trip_to}</span></td>
                                        </tr>
                                        <tr>
                                            <td>Date/Time</td>
                                            <td>
                                                <span>{month} {day}, {year}</span>  <i className="bi-arrow-right"> </i>  
                                                <span>{
                                                        new Date(data.trip_time).getHours() >11 
                                                        ? new Date(data.trip_time).getHours() +':'+ new Date(data.trip_time).getMinutes() + 'PM'
                                                        : new Date(data.trip_time).getHours() +':'+ new Date(data.trip_time).getMinutes() + 'AM'
                                                        }
                                                </span>
                                            </td>
                                        </tr>
                                        <tr>
                                            <td>Shuttle</td>
                                            <td><span>{data.vehicle_name}</span></td>
                                        </tr>
                                        <tr>
                                            <td>Seat No</td>
                                            <td><span>{data.seats.join(', ')}</span></td>
                                        </tr>

                                    </table>
                                </div>
                                <div className="col-md-5 booking-data">
                                    <h4>Fare  Details</h4>
                                    <table className="table">
                                        <tr>
                                            <td>Ticket Fare:</td>
                                            <td className="aln-r">
                                                <del className="naira">N</del>
                                                {(data.total_fare).toLocaleString()}
                                            </td>

                                        </tr>
                                        <tr>
                                            <td>Grand Total:</td>
                                            <td className="aln-r">
                                                <del className="naira">N</del>
                                                {(data.total_fare).toLocaleString()}
                                            </td>
                                        </tr>
                                        <tr style={{'fontWeight':'bold'}}>
                                           <td>Payable Amount:</td>
                                           <td className="aln-r">
                                                   <del className="naira">N</del>
                                               {(data.total_fare).toLocaleString()}
                                           </td>
                                       </tr>
                                   </table>
                               </div>
                            </div>
                            <div className="mt-5 passenger-details">
                                <form>
                                {
                                    data.seats.map((field, index) =>
                                        <div key={index}>
                                            <b className="alert alert-info mb-4">Passenger {index + 1} Details</b>
                                            <div className="row mb-3">

                                                <div className="col">
                                                    <label htmlFor="seat_no">Seat No:</label>
                                                    <select className="form-select" id="seat_no" name="seat_no" value={field.seat_no} onChange={(event) => handleInputChange(index, event)} required>
                                                        <option></option>
                                                        <option>{field}</option>
                                                    </select>
                                                </div>
                                                <div className="col">
                                                    <label htmlFor="fullname">Full Name:</label>
                                                    <input type="text" id="fullname" className="form-control" name="fullname" value={field.fullname} onChange={(event) => handleInputChange(index, event)} />
                                                </div>  
                                            </div>
                                            <div className="row mb-3">
                                                <div className="col">
                                                    <label htmlFor="email">Email:</label>
                                                    <input className="form-control" type="email" name="email" id="email" required placeholder="email@example.com"/>
                                                </div>
                                                <div className="col">
                                                    <label htmlFor="phone">Phone:</label>
                                                    <input className="form-control" type="tel" name="phone" id="phone" required placeholder="08012345678" />
                                                </div>  
                                            </div>
                                            <div className="row mb-3">
                                                <div className="col">
                                                    <label htmlFor="emergencyName">Emergency Contact:</label>
                                                    <input className="form-control" type="text" name="emergencyName" id="emergencyName" required placeholder="Emergency Contact"/>
                                                </div>
                                                <div className="col">
                                                    <label htmlFor="emergencyPhone" className="form-label ">Emergency Phone:</label>
                                                    <input className="form-control" type="tel" name="emergencyPhone" id="emergencyPhone" required placeholder="Emergency Phone" />
                                                </div>  
                                            </div>
                                        </div>
                                    )
                                }
                                 <div className="text-center mb-3">
                                        <button className="btn btn-primary" onClick={submit}>Proceed to Payment</button>
                                    </div>
                                </form>
                            </div>
                        </Col>
                        <Col md={1}></Col>
                    </Row>
            </div>
            <Footer />
            </>
        ) }
        else{ 
        return (<Navigate to="/booking" />) }

    }

export default BookingData;
