import { createContext, useContext, useState } from "react";

const AdminContext = () =>{
    createContext({
    isLoggedIn: false,
    SetIsLoggedIn: undefined,
    user: undefined,
    SetUser: undefined,
    token: undefined,
    SetToken: undefined
})
};

export const AdminProvider = ({ children }) =>{
    return(
        <AdminContext.Provider value={{isLoggedIn, SetIsLoggedIn, user, SetUser, token, SetToken}}>
            {children}
        </AdminContext.Provider>
    )

}
export const useAdminContext = () => useContext(AdminContext);
