import React from "react";
import SideBar from "../components/sidebar";
import axios from "axios";
import { useState , useEffect} from "react";
import Trips from "./tripItems";

const ManageTrips = () =>{
    //const [response, setResponse] = useState([])
    //const trip_item = 
   let response = ''
    function getResponse(e){
        e.preventDefault()
            //setResponse(e.target.value)
             response = e.target.value
        };
    return(
        <>
        <div className="container-fluid">
        <div className="row flex-nowrap">
            <SideBar />
            <div className="col py-5 col-md-9 admin-content">
            <h3 className="text-center home-1st-text text-dark">
                ROUTES, TRIPS & VEHICLE MANAGEMENT <i className="bi bi-sliders"></i>
            </h3>
            <hr />
            <div className="w-50">
                <b>Select Item to Manage: </b>
                <select className="form-select" name="tripItem" required onChange={getResponse}>
                    <option value=""></option>
                    <option value="routes_tbl">Manage Routes</option>
                    <option value="trip_tbl">Manage Trips</option>
                    <option value="vehicle_tbl">Manage Vehicles</option>
                </select>
            </div>
            <div className="mt-5">
                {
                    response === 'trip_tbl' ? <Trips dbTable={response} /> : 'Nothing yet'
                }
            </div>

        </div>
        </div>
        </div> 
        </>
    )
}

export default ManageTrips;
