import React, { useState } from "react";
import { Col, Row} from "react-bootstrap";
import Header from '../components/header';
import Footer from '../components/footer';
import { Navigate, useLocation, useNavigate} from "react-router-dom";
import axios from "axios";
import { useEffect } from "react";

const BookingData = () =>{
    const location = useLocation();
    const history = useNavigate()

    if(location.state)
    {
        const data = location.state;
        const [searchTerm, setSearchTerm] = useState({
            seats: location.state.seats,
            total_fare: location.state.total_fare,
            trip_id: location.state.trip_id
        });


        return(
            <>
            <Header />
            <div className="content">
                    <Row>
                        <Col md={1}></Col>
                        <Col md={10} className="middle-content">
                            <div className="alert alert-info trip-schedule">
                                <p>
                                    Boarding: from <span>{location.state.trip_from}</span> |
                                    to <span>{location.state.trip_to}</span> |
                                    on <span>{location.state.trip_date} </span>
                                    at <span>
                                    {
                                                    new Date(data.trip_time).getHours() >11 
                                                    ? new Date(data.trip_time).getHours() +':'+ new Date(data.trip_time).getMinutes() + 'PM'
                                                    : new Date(data.trip_time).getHours() +':'+ new Date(data.trip_time).getMinutes() + 'AM'
                                                    }
                                    </span>
                                </p>
                                Total Fare <span>{location.state.total_fare} </span>
                                Vehicle <span>{location.state.vehicle} </span>
                            </div>

                        </Col>
                        <Col md={1}></Col>
                    </Row>
            </div>
            <Footer />
            </>
        ) }
        else{ 
        return (<Navigate to="/booking" />) }

    }

export default BookingData;
