import React, { useEffect, useState } from "react";
import axios from "axios";


export function Trips(){
    //const dbTable = 'trip_tbl'
    const [tResponse, setTResponse] = useState([])
    useEffect(() => {
        axios.get("/api/get-trips").then(res =>{
            setTResponse(res.data);
        })
    }, [])
    return(
        <>
            <table className="table bordered">
                <thead>
                    <tr>
                        <th>Trip Name</th>
                        <th>Trip Fare</th>
                        <th>Trip Date</th>
                        <th>Trip Vehicle</th>
                        <th>Trip Driver</th>
                        <th>Trip Type</th>
                        <th>Action</th>
                    </tr>
                </thead>
                <tbody>
                        {
                            tResponse.map(trip =>
                                <tr key={trip.trip_id}>
                                    <td>{trip.trip_name}</td>
                                    <td>&#x20A6;{Intl.NumberFormat('en-US').format(trip.trip_fare)}</td>
                                    <td>{trip.trip_date}</td>
                                    <td>{trip.vehicle_name}</td>
                                    <td>{trip.driver_name}</td>
                                    <td>{trip.trip_type}</td>
                                    <td>
                                        <i className="bi bi-pencil"></i> &nbsp;&nbsp;&nbsp;
                                        <i className="bi bi-trash"></i> &nbsp;&nbsp;&nbsp;
                                        <i className="bi bi-printer-fill"></i>
                                    </td>
                                </tr>
                            )
                        }
                </tbody>
            </table>
        </>
    )
}



export function GetRoutes(){
    //const dbTable = 'trip_tbl'
    const [tResponse, setTResponse] = useState([])
    useEffect(() => {
        axios.get("/api/get-routes").then(res =>{
            setTResponse(res.data);
        })
    }, [])
    return(
        <>
            <table className="table bordered">
                <thead>
                    <tr>
                        <th>Route ID</th>
                        <th>Route From</th>
                        <th>Route To</th>
                        <th>Action</th>
                    </tr>
                </thead>
                <tbody>
                        {
                            tResponse.map(route =>
                                <tr key={route.route_id}>
                                    <td>{route.route_id}</td>
                                    <td>{route.route_from}</td>
                                    <td>{route.route_to}</td>
                                    <td>
                                        <i className="bi bi-pencil"></i> &nbsp;&nbsp;&nbsp;
                                        <i className="bi bi-trash"></i>
                                    </td>
                                </tr>
                            )
                        }
                </tbody>
            </table>
        </>
    )
};

export function Vehicles(){
    //const dbTable = 'trip_tbl'
    const [tResponse, setTResponse] = useState([])
    useEffect(() => {
        axios.get("/api/get-vehicles").then(res =>{
            setTResponse(res.data);
        })
    }, [])
    return(
        <>
            <table className="table bordered">
                <thead>
                    <tr>
                        <th>Vehicle ID</th>
                        <th>Vehicle Name</th>
                        <th>Vehicle Number</th>
                        <th>Action</th>
                    </tr>
                </thead>
                <tbody>
                        {
                            tResponse.map(vehicle =>
                                <tr key={vehicle.vehicle_id}>
                                    <td>{vehicle.vehicle_id}</td>
                                    <td>{vehicle.vehicle_name}</td>
                                    <td>{vehicle.vehicle_number}</td>
                                    <td>
                                        <i className="bi bi-pencil"></i> &nbsp;&nbsp;&nbsp;
                                        <i className="bi bi-trash"></i>
                                    </td>
                                </tr>
                            )
                        }
                </tbody>
            </table>
        </>
    )
};

export function Manifest(){
    //const dbTable = 'trip_tbl'
    const [mResponse, setMResponse] = useState([])
    useEffect(() => {
        axios.get("/api/get-manifest").then(res =>{
            setMResponse(res.data);
        })
    }, [])
    return(
        <>
            <table className="table bordered">
                <thead>
                    <tr>
                        <th>S/N</th>
                        <th>Passenger</th>
                        <th>Sex</th>
                        <th>Phone No.</th>
                        <th>Emergency Contact</th>
                        <th>Emergency Phone</th>
                        <th>Seat No.</th>
                    </tr>
                </thead>
                <tbody>
                        {/*
                            tResponse.map(trip =>
                                <tr key={trip.trip_id}>
                                    <td>{trip.trip_name}</td>
                                    <td>&#x20A6;{Intl.NumberFormat('en-US').format(trip.trip_fare)}</td>
                                    <td>{trip.trip_date}</td>
                                    <td>{trip.vehicle_name}</td>
                                    <td>{trip.driver_name}</td>
                                    <td>{trip.trip_type}</td>
                                    <td>
                                        <i className="bi bi-pencil"></i> &nbsp;&nbsp;&nbsp;
                                        <i className="bi bi-trash"></i> &nbsp;&nbsp;&nbsp;
                                        <i className="bi bi-printer-fill"></i>
                                    </td>
                                </tr>
                            )*/
                        }
                </tbody>
            </table>
        </>
    )
}
