import React from "react";
import { Navbar, Offcanvas, Nav, NavDropdown, Container, } from "react-bootstrap";
import { Link } from "react-router-dom";
import { useState } from 'react';


const Menu = ()=>{
  let expand="sm"
  const [menuOpen, setMenuOpen] = useState(false)
  const toggleMenu = () => {
    setMenuOpen(!menuOpen)
  }

  const handleClose = () => setMenuOpen(false)
    return(

    <>
    <Navbar key={expand} expand={expand} className="mb-3 menu header" fixed="top">
      <Container fluid>
        <Navbar.Brand>
        <Link to="/"><img src='./images/logo.png' className='logo' alt='logo'/></Link>
        </Navbar.Brand>
        <Navbar.Toggle aria-controls={`offcanvasNavbar-expand-${expand}`} onClick={toggleMenu} />
        <Navbar.Offcanvas
          id={`offcanvasNavbar-expand-${expand}`}
          aria-labelledby={`offcanvasNavbarLabel-expand-${expand}`}
          placement="start"
          restoreFocus={false}
          show={menuOpen}
          onHide={handleClose}
        >
          <Offcanvas.Header closeButton>
            <Offcanvas.Title id={`offcanvasNavbarLabel-expand-${expand}`}>
            <Link to="/">CROC-CITY</Link>  
            </Offcanvas.Title>
          </Offcanvas.Header>
          <Offcanvas.Body>
            <Nav className="justify-content-end flex-grow-1 pe-3">
              <Link className="nav-link" to="/booking" onClick={toggleMenu} >BOOKING</Link>
              <Link className="nav-link" to="/cargo">CARGO</Link>
              <Link className="nav-link" to="/terminals">TERMINALS</Link>
              <NavDropdown
                title="SERVICES"
                id={`offcanvasNavbarDropdown-expand-${expand}`}
              >
                <NavDropdown.Item>
                    <Link to="/interstate">Inter-State Travels</Link>
                </NavDropdown.Item>
                <NavDropdown.Divider />
                <NavDropdown.Item>
                    <Link to="/cargo-transport">Cargo & Parcel Transport</Link>
                </NavDropdown.Item>
              </NavDropdown>
              <Link className="nav-link" to="/about">ABOUT</Link>
              <Link className="nav-link" to="/contact">CONTACT</Link>
              <Link className="nav-link" to="/faq">FAQ</Link>
            </Nav>

              <Link  to="/" className=' btn-C'>Login / Sign Up</Link>
          </Offcanvas.Body>
        </Navbar.Offcanvas>
      </Container>
    </Navbar>
    </>
)
}

export default Menu;
