import React, { useState } from "react";
import { Col, Row} from "react-bootstrap";
import Header from '../components/header';
import Footer from '../components/footer';
import { Navigate, useLocation} from "react-router-dom";
import axios from "axios";
import { useEffect } from "react";

const SearchTrips = () =>{
    const location = useLocation();

    if(location.state)
    {
        //const searchTerm = location.state;
        const [searchTerm, setSearchTerm] = useState({
            trip_from: location.state.trip_from,
            trip_to: location.state.trip_to,
            trip_date: location.state.trip_date
        });
        const [searchData, setSearchData] = useState([]);
        const [seatData, setSeatData] = useState([]);
        const months = ["January", "February", "March", "April", "May", "June", "July", "August", "September", "October", "November", "December"];
        const d = new Date(searchTerm.trip_date);
        let month = months[d.getMonth()];
        const year = d.getFullYear();
        const day = d.getDate()
        let photo = 'uploads/vehicle_photos/';



        useEffect(() =>
        {
                axios.post("/api/get-trips", searchTerm).then(res =>{
                setSearchData(res.data);

                    let newSeats = [];
                   res.data.map(s_data => {
                        axios.get(`/api/get-trip-seats/${s_data.trip_id}`).then(response =>{
                        newSeats.push(response.data)
                        //console.log(res.data)
                        setSeatData(newSeats);
                        console.log(newSeats);
                        })   
                    })

                });

        },[]);

        return(
            <>
            <Header />
            <div className="content">
                    <Row>
                        <Col md={1}></Col>
                        <Col md={10} className="middle-content">
                            <div className="alert alert-info trip-schedule">
                                Trip Schedule: from <span>{location.state.trip_from}</span>
                                to <span>{location.state.trip_to} </span>
                                on <span>{month}  {day}, {year}</span>
                            </div>
                            <div className="table-responsive trip-details">
                                <table className="table table-borderless">
                                    <thead>
                                        <tr className="table-purple">
                                        <th>Shuttle Photo</th>
                                        <th>Shuttle Name</th>
                                        <th>Schedule</th>
                                        <th>Fare</th>
                                        <th>Seats Available</th>
                                        <th>View</th>
                                        </tr>
                                    </thead>
                                    <tbody className="accordion" id="accordionExample">
                                        {

                                            searchData.length > 0 ? searchData.map((data, index) =>
                                            <>
                                            <tr key={index} className="accordion-item">
                                                <td><img src={photo+data.vehicle_photo} alt="vehicle photo" className="vehicle_photo" /></td>
                                                <td><b>{data.vehicle_name}</b></td>
                                                <td>
                                                    {
                                                    new Date(data.trip_time).getHours() >11 
                                                    ? new Date(data.trip_time).getHours() +':'+ new Date(data.trip_time).getMinutes() + 'PM'
                                                    : new Date(data.trip_time).getHours() +':'+ new Date(data.trip_time).getMinutes() + 'AM'
                                                    }
                                                    </td>
                                                <td><del className="naira">N</del>{(data.trip_fare).toLocaleString()}</td>
                                                <td>{data.available_seats}/{data.trip_seats}</td>
                                                <td>
                                                    <button className="btn" id="btn-view-seats" data-bs-toggle="collapse" data-bs-target={"#collapse"+index} aria-expanded="true" aria-controls={"collapse"+index}>View Seats</button>
                                                </td>
                                            </tr>
                                            <tr id={"collapse"+index} className="accordion-collapse collapse" aria-labelledby="headingOne" data-bs-parent="#accordionExample">
                                                <td colSpan={6}>
                                                <div className="accordion-body view-seats">
                                                    Select Your Seats.
                                                    {
                                                        seatData.length > 0 ? seatData.map((seats, index) => 
                                                        {
                                                            //console.log(seats.trip_id);

                                                                return(
                                                                    <div key={index}>
                                                                    <h3>Seat: {seats.seat_no}</h3>
                                                                </div>
                                                          )

                                                        }






                                                        )
                                                        :<div className="alert alert-danger">
                                                            No Seats Available
                                                        </div>
                                                    }
                                                </div>
                                                </td>

                                            </tr>
                                            </>
                                            )
                                            : <tr>
                                                <td colSpan={6}> 
                                                    <div className="alert alert-warning trip-schedule">
                                                            Sorry, We Don't Have Any Trips from 
                                                            <span>{location.state.trip_from}</span>
                                                            to <span>{location.state.trip_to} </span>
                                                            on <span>{month}  {day}, {year}</span>
                                                            <p>
                                                                Kindly Check Other Dates.
                                                            </p>
                                                    </div>
                                                </td>


                                            </tr>
                                        }
                                    </tbody>
                                </table>
                            </div>
                        </Col>
                        <Col md={1}></Col>
                    </Row>
            </div>
            <Footer />
            </>
        ) }
        else{ 
        return (<Navigate to="/booking" />) }

    }

export default SearchTrips;
