import React from "react";
import SideBar from "../components/sidebar";
import axios from "axios";
import { useState , useEffect} from "react";

const ManageTrips = () =>{
    const [trip, setTrip] = useState({
        tripFrom: '',
        tripTo: '',
        tripFare: '',
        tripDate: '',
        tripVehicle: '',
        tripDriver: '',
    })
    const [response, setResponse] = useState([])
    const [from, setFrom] = useState([])
    const [to, setTo] = useState([])
    const [vehicle, setVehicle] = useState([])
    const [driver, setDriver] = useState([])

    useEffect(() => {
        axios.get("/api/get-from-routes").then(res =>{
            setFrom(res.data)
        });

        axios.get("/api/get-vehicles").then(res =>{
            setVehicle(res.data)
        });

        axios.get("/api/get-drivers").then(res =>{
            setDriver(res.data)
        });
    },[],[],[])

    const getOneRoute = (e) =>{
      //setFromRoute({...fromRoute, [e.target.name]:e.target.value})
      const fromRoute = {[e.target.name]: e.target.value};
      if(fromRoute !== '')
        {
          //console.log(fromRoute)
        axios.post("/api/get-to-routes", fromRoute).then(res =>{
          setTo(res.data)
          //console.log({todata: res.data})
        });
      }
      else console.log("No from Route Set")
    }

    const setValues = (e) =>{
        setTrip({...trip, [e.target.name]:e.target.value})
        //console.log(trip)
    }

    function getTripItem(e){
        let item = {[e.target.name]:e.target.value};
        e.preventDefault()
        axios.post("/api/get-trip-item", item).then(res =>{
            setResponse(res.data)
        })
    }
    return(
        <>
        <div className="container-fluid">
        <div className="row flex-nowrap">
            <SideBar />
            <div className="col py-5 col-md-9 admin-content">
            <h3 className="text-center home-1st-text text-dark">
                ROUTES, TRIPS & VEHICLE MANAGEMENT <i className="bi bi-sliders"></i>
            </h3>
            <hr />
            <div className="col-md-3">
                <b>Select Item to Manage: </b>
                <select className="form-select" name="tripItem" required onChange={getTripItem}>
                    <option value=""></option>
                    <option value="routes_tbl">Manage Routes</option>
                    <option value="trip_tbl">Manage Trips</option>
                    <option value="vehicle_tbl">Manage Vehicles</option>
                </select>
            </div>

        </div>
        </div>
        </div> 
        </>
    )
}

export default ManageTrips;
