import React, { useEffect, useState, useRef } from "react";
import SideBar from "../components/sidebar";
import axios from "axios";
import { Manifest } from "./tripItems";
import { useParams } from "react-router-dom";
import {useReactToPrint} from 'react-to-print'

const PrintManifest = () =>{
    const id = useParams()
    const tID = id.tripID 
    const componentRef = useRef();

    const printPage = () =>{
        useReactToPrint({ content: () => componentRef.current})
    }
return(
    <>

    <div className="container-fluid">
        <div className="row flex-nowrap">
            <SideBar />
            <div className="col py-5 col-md-9 admin-content table-responsive">
            <Manifest trip_id={tID} ref={componentRef} />
        <button className="btn btn-primary" onClick={printPage}>Print Manifest</button>
        </div>
        </div>
        </div> 
    </>
)
}

export default PrintManifest;
