import { useState } from 'react';

export default function useLogin() {
  const getLogin = () => {
    const loginString = localStorage.getItem('login');
    const userLogin = JSON.parse(loginString);
    if(useLogin){
      return login;
    }
  };

  const [login, setLogin] = useState(getLogin());

  const saveLogin = userLogin => {
    localStorage.setItem('login', JSON.stringify(userLogin));
    setLogin(userLogin.login);
  };

  return {
    setLogin: saveLogin,
    login
  }
}
