import React from "react";
import { useState } from "react";
import axios from "axios";

const AdminLogin = () =>{
    const [loginInfo, setLoginInfo] = useState({
        username: '',
        password: ''
    })

    const [adminData, setAdminData] = useState([])

    const setValue = (e) =>{
        setLoginInfo({...loginInfo, [e.target.name]:e.target.value});
        console.log(loginInfo)
    }

    const Login = (e) =>{
        e.preventDefault()
        axios.post("/api/login", loginInfo).then((res) =>{
            setAdminData(res.data.loginData)
            console.log(res.data.loginData)
        }

        )
    }
    return(
        <>
        <div className="container">
            <div className="row">
                <div className="col-md-2"></div>
                <div className="col-md-8 mt-5 admin-login">
                    <center><img src="./images/logo.png" alt="logo" className="align-self-center" /></center>
                    <h3>Admin Panel</h3>
                    <hr />
                    <form name="adminLogin"   className="form" onSubmit={Login} encType="multipart/form-data">
                        <p>
                            <b>Username: </b>
                            <input type="text" className="form-control w-50" placeholder=" Enter Username" 
                            name="username" onChange={setValue} value={loginInfo.username} /> 
                         </p> 
                         <p>
                         <b>Password: </b>
                            <input type="password" className="form-control w-50" placeholder="Enter Password" 
                            name="password" onChange={setValue} value={loginInfo.password} /> 
                         </p> 
                         <p>
                            <button type="submit" className="btn-subscribe form-control w-25" name="submitLogin">Login</button>
                         </p>     
                    </form>
                </div>
                <div className="col-md-2"></div>
            </div>
        </div> 
        </>
    )
}

export default AdminLogin;
