import React, { useState } from "react";
import { Col, Row} from "react-bootstrap";
import Header from '../components/header';
import Footer from '../components/footer';
import { Navigate, useLocation} from "react-router-dom";
import axios from "axios";
import { useEffect } from "react";

const SearchTrips = () =>{
    const location = useLocation();

    if(location.state)
    {
        //const searchTerm = location.state;
        const [searchTerm, setSearchTerm] = useState({
            trip_from: location.state.trip_from,
            trip_to: location.state.trip_to,
            trip_date: location.state.trip_date
        });
        const [searchData, setSearchData] = useState([]);
        const months = ["January", "February", "March", "April", "May", "June", "July", "August", "September", "October", "November", "December"];
        const d = new Date(searchTerm.trip_date);
        let month = months[d.getMonth()];
        const year = d.getFullYear();
        const day = d.getDate()
        let photo = 'uploads/vehicle_photos/';
        useEffect(() => {
            async function Search()
            {
                await axios.post("/api/get-trips", searchTerm).then(res =>{
                setSearchData(res.data);
                //console.log(searchData)
                //console.log(res.data)
                });
            }
            Search();
        },[])
        return(
            <>
            <Header />
            <div className="content">
                    <Row>
                        <Col md={1}></Col>
                        <Col md={10} className="middle-content">
                            <div className="alert alert-info trip-schedule">
                                Trip Schedule: from <span>{location.state.trip_from}</span>
                                to <span>{location.state.trip_to} </span>
                                on <span>{month}  {day}, {year}</span>
                            </div>
                            <div className="table-responsive">
                                <table className="table">
                                    <thead>
                                        <tr className="table-purple">
                                        <th>Shuttle Photo</th>
                                        <th>Shuttle Name</th>
                                        <th>Departure Time</th>
                                        <th>Fare</th>
                                        <th>Seats Available</th>
                                        <th>View</th>
                                        </tr>
                                    </thead>
                                    <tbody>
                                        {

                                            searchData.map((data) =>
                                            <tr key={data.trip_id}>
                                                <td><img src={photo+data.vehicle_photo} alt="vehicle photo" className="vehicle_photo" /></td>
                                                <td>{data.vehicle_name}</td>
                                                <td>
                                                    {
                                                    data.trip_time.getHour() >11 
                                                    ? data.trip_time.getHour() +':'+ data.trip_time.getMinutes() + 'PM'
                                                    : data.trip_time.getHour() +':'+ data.trip_time.getMinutes() + 'AM'
                                                    }
                                                    </td>
                                                <td>{data.trip_fare}</td>
                                                <td>{data.trip_seats}</td>
                                                <td><i className="bi bi-eye"></i></td>
                                            </tr>
                                            )
                                        }
                                    </tbody>
                                </table>
                            </div>
                        </Col>
                        <Col md={1}></Col>
                    </Row>
            </div>
            <Footer />
            </>
        ) }
        else{ 
        return (<Navigate to="/booking" />) }

    }

export default SearchTrips;
